import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const DishDetails = () => {
  const { id } = useParams();
  const [dishDetails, setDishDetails] = useState(null);

    useEffect(() => {
    // Fetch dish details based on the ID
    // Example: axios.get(`/api/dishes/${id}`).then(response => setDishDetails(response.data));
        try {
            axios.get('http://localhost:5000/recipe/${id}').then(response => setDishDetails(response.data));
        } catch (error) {
            console.error('Error fetching recipes:', error);
        }

    }, [id]);

    return (
        <div>
            {dishDetails ? (
                <div>

                    <div>
                        {dish.image && <img src={dish.image} alt={dish.title} />}
                        <h1>{dish.title}</h1>
                        <h2>Ingredients</h2>
                        <ul className="ingredient-list">
                            {dish.extendedIngredients.map(ingredient => (
                            <li key={ingredient.id}>{ingredient.original}</li>
                            ))}
                        </ul>

                    <h2>Instructions</h2>
                    <ol>
                        {dish.analyzedInstructions[0].steps.map((step, index) => (
                        <li key={index}>{step.step}</li>
                        ))}
                    </ol>
                    </div>
                    <a href={`/?search_query=${searchQuery}`}>Back to search results</a>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default DishDetails;
