import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios'
import { Link } from 'react-router-dom';

const DishDetails = () => {
  const { id } = useParams();
  const [dishDetails, setDishDetails] = useState(null);

    useEffect(() => {
    // Fetch dish details based on the ID
    // Example: axios.get(`/api/dishes/${id}`).then(response => setDishDetails(response.data));
        try {
            axios.get(`http://localhost:5000/recipe/${id}`).then(response => setDishDetails(response.data));
        } catch (error) {
            console.error('Error fetching recipes:', error);
        }

    }, [id]);

    return (
        <div>
            {dishDetails ? (
                <div>

                    <div>
                        {dishDetails.image && <img src={dishDetails.image} alt={dishDetails.title} />}
                        <h1>{dishDetails.title}</h1>
                        <h2>Ingredients</h2>
                        <ul className="ingredient-list">
                            {dishDetails.extendedIngredients.map(ingredient => (
                            <li key={ingredient.id}>{ingredient.original}</li>
                            ))}
                        </ul>

                    <h2>Instructions</h2>
                    <ol>
                        {dishDetails.analyzedInstructions[0].steps.map((step, index) => (
                        <li key={index}>{step.step}</li>
                        ))}
                    </ol>
                    </div>
                    <Link to={`/`}>Back to Search Result</Link>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default DishDetails;
